import React, { useState } from "react";
import { getFirestore, collection, doc, deleteDoc } from 'firebase/firestore';
import firebaseConfig from './firebaseConfig';
import { initializeApp } from 'firebase/app';

export default function Produtos(props) {
    const { produtos } = props;
    const [modalOpen, setModalOpen] = useState(true); // Estado para controlar a exibição do modal
    const [produtoSelecionado, setProdutoSelecionado] = useState(null); // Estado para armazenar o produto selecionado

    const excluirProduto = async (id) => {
        try {
            const app = initializeApp(firebaseConfig);
            const db = getFirestore(app);
            const produtoRef = doc(db, 'produtos', id);
            await deleteDoc(produtoRef);
            console.log("Produto excluído com sucesso!");
            setModalOpen(false); // Feche o modal após excluir o produto
        } catch (error) {
            console.error("Erro ao excluir o produto:", error);
        }
    };

    const handleDeleteClick = (produto) => {
        setProdutoSelecionado(produto);
        setModalOpen(true); // Abra o modal quando o botão de exclusão for clicado
    };

    return (
        <div className="Produtos">
            <div className="tabela-de-produtos">
                <p className="coluna">Nome</p>
                <p className="coluna">Preço de Fábrica</p>
                <p className="coluna">IPI</p>
                <p className="coluna">ICMS</p>
                <p className="coluna">LARGURA</p>
                <p className="coluna">COMPRIMENTO</p>
                <p className="coluna">ALTURA</p>
                <p className="coluna">Buttons</p>
            </div>
            <div className="container-produtos">
                {produtos.map(produto => (
                    <div className="produto" key={produto.id}>
                        <p className="coluna">{produto.Nome}</p>
                        <p className="coluna">{produto.Preco_de_Fabrica}</p>
                        <p className="coluna">{produto.IPI}</p>
                        <p className="coluna">{produto.ICMS}</p>
                        <p className="coluna">{produto.Largura}</p>
                        <p className="coluna">{produto.Comprimento}</p>
                        <p className="coluna">{produto.Altura}</p>
                        <p className="coluna">
                            <div className="productButtons">
                                <button onClick={() => handleDeleteClick(produto)} className="deleteBtn" data-id={produto.id}>X</button>
                                <button className="editBtn" data-id={produto.id}>Editar</button>
                            </div>
                        </p>
                    </div>
                ))}
            </div>
            {modalOpen && (
                <div className="modal modalExclude">
                    <div>
                    <p>{`Tem certeza que deseja excluir o Produto ${produtoSelecionado?.Nome}?`}</p>
                    <button onClick={() => excluirProduto(produtoSelecionado.id)}>Sim</button>
                    <button onClick={() => setModalOpen(false)}>Não</button>
                    </div>


                </div>
            )}
        </div>
    );
}
