import React, { useEffect, useState } from "react";
import { getFirestore, collection, getDocs } from "firebase/firestore";
import firebaseConfig from "./firebaseConfig";

export default function ConfirmarProduto(props) {
    const { show, handleCloseModal, handleCancelar } = props;
    const [produtos, setProdutos] = useState([]);

    useEffect(() => {
        const fetchProdutos = async () => {
            const db = getFirestore();
            const produtosRef = collection(db, "produtos");
            const produtosSnapshot = await getDocs(produtosRef);
            const produtosData = produtosSnapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));
            setProdutos(produtosData);
        };

        fetchProdutos();
    }, []);

    return (
        <div className={show}>
            <div className="modal-confirmacao">
                <h2 className="modal-title">Dados do Produto:</h2>
                <div className="dados-produto">
                    {produtos.map(produto => (
                        <div key={produto.id}>
                            <h4>Nome: {produto.nome}</h4>
                            <h4>Preço de Fábrica: {produto.precoDeFabrica}</h4>
                            <h4>IPI: {produto.IPI}</h4>
                            <h4>ICMS: {produto.ICMS}</h4>
                            <h4>Largura: {produto.Largura}</h4>
                            <h4>Comprimento: {produto.Comprimento}</h4>
                            <h4>Altura: {produto.Altura}</h4>
                        </div>
                    ))}
                </div>

                <div className="modal-confirm">
                    <h1>As informações estão corretas?</h1>
                    <div className="modal-buttons">
                        <button className="no-button" onClick={handleCancelar}>Não</button>
                        <button className="yes-button" onClick={handleCloseModal}>Sim</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
