import React, { useState } from "react";
import Response from "./Response";

export default function RespostaSimulacao({ produtos }) {
  const [lucroDesejado, setLucroDesejado] = useState(0);
  const [pesoMaximo, setPesoMaximo] = useState(0);
  const [capacidadeCaminhao, setCapacidadeCaminhao] = useState(0);
  const [combinações, setCombinações] = useState([]);

  // Função para encontrar todas as combinações possíveis de produtos
  const encontrarCombinações = () => {
    const combinações = [];

    // Função para calcular o lucro total de uma combinação de produtos
    const calcularLucroCombinação = (combinação) => {
      return combinação.reduce((total, produto) => total + produto.precoVenda - produto.precoFinal, 0);
    };

    // Função auxiliar para gerar todas as combinações possíveis
    const gerarCombinações = (produtos, combinaçãoAtual, index) => {
      if (index === produtos.length) {
        // Verifica se a combinação atual atende aos critérios de peso e capacidade do caminhão
        const pesoTotal = combinaçãoAtual.reduce((total, produto) => total + produto.peso, 0);
        const volumeTotal = combinaçãoAtual.reduce((total, produto) => total + produto.m3, 0);
        if (pesoTotal <= pesoMaximo && volumeTotal <= capacidadeCaminhao) {
          combinações.push(combinaçãoAtual);
        }
        return;
      }

      // Inclui o produto atual na combinação e chama recursivamente a função para o próximo produto
      gerarCombinações(produtos, combinaçãoAtual.concat(produtos[index]), index + 1);
      // Chama recursivamente a função sem incluir o produto atual na combinação
      gerarCombinações(produtos, combinaçãoAtual, index + 1);
    };

    // Inicializa a geração de combinações
    gerarCombinações(produtos, [], 0);

    // Filtra combinações que atingem o lucro desejado
    const combinaçõesFiltradas = combinações.filter(combinação => calcularLucroCombinação(combinação) >= lucroDesejado);

    // Define as combinações encontradas no estado 'combinações'
    setCombinações(combinaçõesFiltradas);
  };

  return (
    <div className="RespostaSimulacao">
      <div className="RespostaSimulacaoHeader">
        <h1 className="SimulacoesTitle">SIMULAÇÕES</h1>
      </div>
      <div>
        <h2>Quanto de lucro deseja ganhar?</h2>
        <input
          type="number"
          value={lucroDesejado}
          onChange={(e) => setLucroDesejado(parseInt(e.target.value))}
        />
        <h3>Peso máximo do caminhão</h3>
        <input
          type="number"
          value={pesoMaximo}
          onChange={(e) => setPesoMaximo(parseInt(e.target.value))}
        />
        <h3>Capacidade total do caminhão</h3>
        <input
          type="number"
          value={capacidadeCaminhao}
          onChange={(e) => setCapacidadeCaminhao(parseInt(e.target.value))}
        />
        <button onClick={encontrarCombinações}>Gerar Possibilidades</button>
      </div>
      <div>
        {combinações.map((combinação, index) => (
          <Response key={index}>
            <p>Combinação {index + 1}:</p>
            <ul>
              {combinação.map((produto) => (
                <li key={produto.id}>{produto.nome}</li>
              ))}
            </ul>
            <p>Lucro: R$ {calcularLucroCombinação(combinação)}</p>
          </Response>
        ))}
      </div>
    </div>
  );
}
