import React from "react";

export default function RespostaSimulacao({ combinations }) {
  console.log(combinations)
  return (
    <div className="RespostaSimulacao">
      <div className="RespostaSimulacaoHeader">
        <img
          className="moneyIcon"
          src="https://static.vecteezy.com/system/resources/previews/011/660/689/original/3d-money-icon-illustration-png.png"
          alt="Ícone de dinheiro"
        />
        <h1 className="SimulacoesTitle">SIMULAÇÕES</h1>
        <img
          className="moneyIcon"
          src="https://static.vecteezy.com/system/resources/previews/011/660/689/original/3d-money-icon-illustration-png.png"
          alt="Ícone de dinheiro"
        />
      </div>

      {combinations.map((combinacao, index) => (
        <div className="combinacao" key={index}>
          {/* Renderizar lista de produtos */}
          {combinacao.map((produto, produtoIndex) => {
            if (produtoIndex < combinacao.length - 3) {
              return (
                <div className="produto-da-combinacao" key={produtoIndex}>
                  <h3>{produto.nome}</h3>
                  <h4>QTDE: {produto.qtde}</h4>
                  <h4>Custo uni: R${produto.custoUnitario}</h4>
                  <h4>Custo Total: R${(produto.custoUnitario * produto.qtde).toFixed(2)}</h4>
                  <h4>Valor uni: R${produto.vendaUnitario}</h4>
                  <h4>Lucro uni: R${produto.lucroUnitario}</h4>
                  <h4>Volume: {produto.volume}m³</h4>
                </div>
              );
            }
            return null;
          })}
          {/* Renderizar soma dos custos */}
          <div key={`${index}-valores`} className="valores">
            <h2>SOMA CUSTO: R${combinacao[combinacao.length - 2].SOMA_CUSTO}</h2>
            <h2>SOMA LUCRO: R${combinacao[combinacao.length - 3].SOMA}</h2>
            {/* <h2>SOMA VOLUME: R${combinacao[combinacao.length-3].SOMA_CUSTO}</h2> */}
          </div>
        </div>
      ))}

    </div>
  );
}
