import { getFirestore, addDoc, collection } from 'firebase/firestore';
import { initializeApp } from 'firebase/app';
import firebaseConfig from './firebaseConfig';

export default function ConfirmarProduto(props) {
    const { nome, precoDeFabrica, IPI, ICMS, Largura, Comprimento, Altura, show, handleCloseModal, handleCancelar, setPopUpMessage } = props;
    const app = initializeApp(firebaseConfig);
    const db = getFirestore(app);

    const handleConfirmar = async () => {

        try {
            // Adicione um novo documento à coleção 'produtos' com os dados do formulário
            await addDoc(collection(db, 'produtos'), {
                Nome: nome,
                Preco_de_Fabrica: precoDeFabrica,
                IPI: IPI,
                ICMS: ICMS,
                Largura: Largura,
                Comprimento: Comprimento,
                Altura: Altura
            });

            // Feche o modal após o sucesso da criação do produto
            handleCloseModal();
            
            // Defina a mensagem de sucesso
            setPopUpMessage("Produto adicionado com sucesso");
        } catch (error) {
            console.error('Erro ao adicionar produto:', error);
            // Defina a mensagem de erro
            setPopUpMessage("Erro ao adicionar Produto");
        }
    };

    return (
        <div className={show}>
            {/* Restante do seu componente ConfirmarProduto */}
        </div>
    );
}
