// App.js
import './App.css';
import React, { useState } from 'react';
import Navbar from './Components/Navbar';
import Formulario from './Components/Formulario';
import Gerenciamento from './Gerenciamento';
import Carousel from './Components/Carousel';

function App() {
  const [activeComponent, setActiveComponent] = useState('Formulario'); // Defina o componente inicial

  // Função para lidar com o clique nos links do Navbar
  const handleLinkClick = (name) => {
    setActiveComponent(name);
  };

  return (
    <div className="App">
      <Navbar
        brand="Logo."
        links={[
          { name: "Formulario" },
          { name: "Gerenciamento" },
          { name: "Ob" }
        ]}
        onLinkClick={handleLinkClick} // Passar a função de clique para o Navbar
      />

      <div className="container">
        {activeComponent === 'Formulario' && <Formulario />}
        {activeComponent === 'Gerenciamento' && <Gerenciamento />}
        {activeComponent === 'Carousel' && <Carousel />}
      </div>
    </div>
  );
}

export default App;
