import React from "react";

export default function RespostaSimulacao({ combinations }) {

  let combinations_master = [];
  let combinacao_master = [];

  for(let combinacao of combinations){
    combinacao.forEach((p, index) => {

      if(index < (combinacao.length - 2)){
        let objeto = {
          produto: p.nome,
          qtde: p.qtde,
          c_uni: p.custoUnitario,
          v_uni: p.vendaUnitario,
          c_t: p.custoUnitario * p.qtde,
          v_t: p.vendaUnitario * p.qtde,
          lucro_uni: p.lucroUnitario,
          lucro_t: p.lucroUnitario * p.qtde
        }
        combinacao_master.push(objeto)
      }
      
    })
  }

  return (
    <div className="RespostaSimulacao">
      <div className="RespostaSimulacaoHeader">
        <img
          className="moneyIcon"
          src="https://static.vecteezy.com/system/resources/previews/011/660/689/original/3d-money-icon-illustration-png.png"
          alt="Ícone de dinheiro"
        />
        <h1 className="SimulacoesTitle">SIMULAÇÕES</h1>
        <img
          className="moneyIcon"
          src="https://static.vecteezy.com/system/resources/previews/011/660/689/original/3d-money-icon-illustration-png.png"
          alt="Ícone de dinheiro"
        />
      </div>

      {combinations.map((comb, index) => (
        <div className="combinacao" key={index}>
          <h3>Combinação {index + 1}:</h3>
          <p>{comb.nome}</p>
          <p>Qtde: {comb.qtde}</p>
          <p>Volume Total: {comb.volume_t}m³</p>
          <p>Peso Total: {comb.peso_t}kg</p>
          <p>Custo Unitário: R${comb.custo_uni}</p>
          <p>Venda Unitária: R${comb.venda_uni}</p>
          <p>Lucro Unitário: R${comb.lucro_uni}</p>
          <p>Lucro Total: R${comb.lucro_t}</p>
        </div>
      ))}
    </div>
  );
}
