import { getFirestore, addDoc, collection } from 'firebase/firestore';
import { initializeApp } from 'firebase/app';
import firebaseConfig from './firebaseConfig';
import React, { useState } from "react";

export default function ConfirmarProduto(props) {
    const { nome, precoDeFabrica, IPI, ICMS, Largura, Comprimento, Altura, show, handleCloseModal, handleCancelar } = props;
    const db = getFirestore(app);
    
    // Definindo estados para os valores dos campos do formulário
    const [nomeProduto, setNomeProduto] = useState("");
    const [precoDeFabricaProduto, setPrecoDeFabricaProduto] = useState("");
    const [IPIProduto, setIPIProduto] = useState("");
    const [ICMSProduto, setICMSProduto] = useState("");
    const [LarguraProduto, setLarguraProduto] = useState("");
    const [ComprimentoProduto, setComprimentoProduto] = useState("");
    const [AlturaProduto, setAlturaProduto] = useState("");

    const handleConfirmar = async () => {
        try {
            // Adicione um novo documento à coleção 'produtos' com os dados do formulário
            await addDoc(collection(db, 'produtos'), {
                Nome: nomeProduto,
                Preco_de_Fabrica: precoDeFabricaProduto,
                IPI: IPIProduto,
                ICMS: ICMSProduto,
                Largura: LarguraProduto,
                Comprimento: ComprimentoProduto,
                Altura: AlturaProduto
            });

            // Limpa os campos do formulário definindo os estados para vazio
            setNomeProduto("");
            setPrecoDeFabricaProduto("");
            setIPIProduto("");
            setICMSProduto("");
            setLarguraProduto("");
            setComprimentoProduto("");
            setAlturaProduto("");

            // Feche o modal após o sucesso da criação do produto
            handleCloseModal();
        } catch (error) {
            console.error('Erro ao adicionar produto:', error);
        }
    };

    return (
        <div className={show}>
            <div className="modal-confirmacao">
                <h2 className="modal-title">Dados do Produto:</h2>
                <div className="dados-produto">
                    <h4>Nome: {nome}</h4>
                    <h4>Preço de Fábrica: {precoDeFabrica}</h4>
                    <h4>IPI: {IPI}</h4>
                    <h4>ICMS: {ICMS}</h4>
                    <h4>Largura: {Largura}</h4>
                    <h4>Comprimento: {Comprimento}</h4>
                    <h4>Altura: {Altura}</h4>
                </div>

                <div className="modal-confirm">
                    <h1>As informações estão corretas?</h1>
                    <div className="modal-buttons">
                        <button className="no-button" onClick={handleCancelar}>Não</button>
                        <button className="yes-button" onClick={handleConfirmar}>Sim</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
