import React, { useState } from "react";

function Navbar(props) {
    const { brand, links } = props;
    const [activeLink, setActiveLink] = useState(links[0].name); // Defina o primeiro link como ativo inicialmente

    const handleClick = (linkName) => {
        setActiveLink(linkName); // Atualize o link ativo quando clicado
    };

    return (
        <div className="navbar">
            <div className="navbar-nav">
                <a className="nav-brand">{brand}</a>
                <div className="nav-items">
                    {links.map((link, index) => (
                        <a
                            key={index}
                            className={`nav-link ${link.name === activeLink ? 'active' : ''}`}
                            onClick={() => handleClick(link.name)}
                        >
                            {link.name}
                        </a>
                    ))}
                </div>
            </div>
        </div>
    )
}

export default Navbar;
