  apiKey: "AIzaSyAY218g9OR7xsbdyBW8U7ZPrwdxhMY1jXk",
  authDomain: "produtos-bc6da.firebaseapp.com",
  projectId: "produtos-bc6da",
  storageBucket: "produtos-bc6da.appspot.com",
  messagingSenderId: "347261816815",
  appId: "1:347261816815:web:77e65dd7a6124d0270301a"
};


export default function ConfirmarProduto(props) {
