import React from "react";

export default function RespostaSimulacao(props) {

  const {combinacoesUnitarias, combinations, frete} = props;
  

  if (combinations[0] !== null && combinations[0] !== undefined) {
    for(let ix = 0; ix < combinations.length; ix++){
      console.log(combinations[ix])
    }
  }

  return (
    <div className="RespostaSimulacao">
      <div className="RespostaSimulacaoHeader">
        <img
          className="moneyIcon"
          src="https://static.vecteezy.com/system/resources/previews/011/660/689/original/3d-money-icon-illustration-png.png"
          alt="Ícone de dinheiro"
        />
        <h1 className="SimulacoesTitle">SIMULAÇÕES</h1>
        <img
          className="moneyIcon"
          src="https://static.vecteezy.com/system/resources/previews/011/660/689/original/3d-money-icon-illustration-png.png"
          alt="Ícone de dinheiro"
        />
        
      </div>
      *Os valores de custo e lucro estão incluindo o valor do frete

      {combinacoesUnitarias.map((produto, produtoIndex) => {
        return (
          <div className="combinacao" key={produtoIndex}>
            <div className="produto-da-combinacao" key={produtoIndex}>
              <h3>{produto.nome}  -  {produto.qtde.toLocaleString()} unidades</h3>
              <h4>Valor Custo: R${produto.custoUnitario.toFixed(2).toLocaleString()}</h4>
              <h4>Valor Venda: R${produto.vendaUnitario.toFixed(2).toLocaleString()}</h4>
              <h4>Volume: {produto.volume.toFixed(2).toLocaleString()}m³</h4>
              <div key={`${produto.nome}-a`} className="valores">
                <div className="div-flex">
                  <h3 className="custo">CUSTO TOTAL: R${(produto.custoUnitario * produto.qtde).toFixed(2).toLocaleString()}</h3>
                  <h3>LUCRO TOTAL: R${(produto.lucroUnitario * produto.qtde).toFixed(2).toLocaleString()}</h3>
                </div>
                <div className="div-flex">
                  <h4>VOLUME: {produto.volume.toFixed(2).toLocaleString()}m³</h4>
                  <h4>PESO: {produto.peso.toFixed(2).toLocaleString()}Kg</h4>
                </div>
              </div>
            </div>
          </div>
        );
      })}

      {combinations[0] !== null && combinations[0] !== undefined && (
        (() => {
          const elements = [];
          for (let ix = 0; ix < combinations.length; ix++) {
            const combinacao = combinations[ix];
            elements.push(
              <div className="combinacao" key={ix}>
                {/* Aqui você pode adicionar o conteúdo desejado para cada combinação */}
              </div>
            );
          }
          return elements;
        })()
      )}
      

    </div>
  );
}
