import React, { useEffect, useState } from 'react';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import { initializeApp } from 'firebase/app'; 
import firebaseConfig from './Components/firebaseConfig'; 
import RespostaSimulacao from './Components/RespostaSimulacao';

function Simulacao() {
    const [produtos, setProdutos] = useState([]);
    const [lucroDesejado, setLucroDesejado] = useState('');
    const [pesoMaximo, setPesoMaximo] = useState('');
    const [capacidadeCaminhao, setCapacidadeCaminhao] = useState('');

    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                const app = initializeApp(firebaseConfig);
                const db = getFirestore(app);
                const produtosRef = collection(db, 'produtos');
                const produtosSnapshot = await getDocs(produtosRef);
                const produtosData = produtosSnapshot.docs.map(doc => ({
                    id: doc.id,
                    ...doc.data()
                }));
                setProdutos(produtosData);
            } catch (error) {
                console.error('Erro ao buscar os produtos:', error);
            }
        };
        fetchProdutos();
    }, []); 

    let info_products = []


    const handleGerarPossibilidades = () => {
        produtos.forEach(p => {
            let p_obj = {
                custo:parseFloat(p.PRECO_FINAL),
                nome: p.Nome,
                peso: parseFloat(p.Peso),
                volume: parseFloat(p.m3)
            }
            info_products.push(p_obj)
        })
        encherCaminhaoComUmProduto(info_products);
    };

    function encherCaminhaoComUmProduto(objts){
        objts.forEach((obj, index) => {
            let peso_objeto = obj.peso;
            let volume_objeto = obj.volume;
            let peso_c_atual = 0;
            let volume_c_atual = 0;
            let MaxPeso = parseFloat(pesoMaximo);
            let MaxVol = parseFloat(capacidadeCaminhao);
            let qtde_produtos = 0;
            
            console.log(peso)
            while(peso_c_atual < MaxPeso && volume_c_atual < MaxVol){

                if((peso_c_atual + peso_objeto) < MaxPeso && (volume_c_atual + volume_objeto) < MaxVol){
                    qtde_produtos++;
                }else{
                    console.log(`${obj.nome}: ${qtde_produtos} unidades`);
                    peso_c_atual = MaxPeso;
                    volume_c_atual = MaxPeso;
                    qtde_produtos = 0;
                }
            }
        });
    }

    
    return (
        <div className='Simulacao'>
            <h1 className='custom-cursor Simulacao-title'>Simulação De Lucro</h1>
            <div className='PriceWannaWin'>
                <h2>Quanto de lucro deseja ganhar?</h2>
                <input 
                    type='number' 
                    placeholder='Informe o lucro desejado' 
                    value={lucroDesejado}
                    onChange={(e) => setLucroDesejado(e.target.value)}
                />
                <h3>Peso máximo caminhão</h3>
                <input 
                    type='number' 
                    placeholder='Informe o peso máximo'
                    value={pesoMaximo}
                    onChange={(e) => setPesoMaximo(e.target.value)} 
                />
                <h3>Capacidade</h3>
                <input 
                    type='number' 
                    placeholder='Informe a capacidade do caminhão'
                    value={capacidadeCaminhao}
                    onChange={(e) => setCapacidadeCaminhao(e.target.value)} 
                />
                <button onClick={handleGerarPossibilidades}>Gerar Possibilidades</button>
            </div>
            <RespostaSimulacao />
        </div>
    );
}

export default Simulacao;
