import React, { useEffect, useState } from 'react';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import { initializeApp } from 'firebase/app'; 
import firebaseConfig from './Components/firebaseConfig'; 
import RespostaSimulacao from './Components/RespostaSimulacao';

function Simulacao() {
    const [produtos, setProdutos] = useState([]);
    const [lucroDesejado, setLucroDesejado] = useState('');
    const [capacidade, setCapacidade] = useState('');
    const [VolumeCaminhao, setVolumeCaminhao] = useState('');
    const [maximoLucroUni, setMaximoLucroUni] = useState('');
    const [combinations, setCombinations] = useState([]);

    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                const app = initializeApp(firebaseConfig);
                const db = getFirestore(app);
                const produtosRef = collection(db, 'produtos');
                const produtosSnapshot = await getDocs(produtosRef);
                const produtosData = produtosSnapshot.docs.map(doc => ({
                    id: doc.id,
                    ...doc.data()
                }));
                setProdutos(produtosData);
            } catch (error) {
                console.error('Erro ao buscar os produtos:', error);
            }
        };
        fetchProdutos();
    }, []); 

    const handleGerarPossibilidades = () => {
        const info_products = produtos.map(p => ({
            custo: parseFloat(p.PRECO_FINAL),
            nome: p.Nome,
            peso: parseFloat(p.Peso),
            volume: parseFloat(p.m3)
        }));
    
        let TODAS_AS_COMBINACOES = new Set();
        let tentativas = 0;
        
        while (TODAS_AS_COMBINACOES.size < 100 && tentativas < 2500) {
            let combinacao2 = encherCaminhao(info_products);
            if (combinacao2) {
                TODAS_AS_COMBINACOES.add(JSON.stringify(combinacao2));
            }
            tentativas++;
        }

        let masterCombinacoes = [];
        Array.from(TODAS_AS_COMBINACOES).map(comb =>{
            masterCombinacoes.push(JSON.parse(comb))
        });
        console.log(se)
        setCombinations(masterCombinacoes)

    };


    useEffect(() => {
        console.log(combinations);
    }, [combinations]);

    function encherCaminhao(objects){
        let random_nums = []
        for(let i = 0; i < 6; i++){
            random_nums[i] = Math.floor(Math.random() * objects.length);
        }


        let aparicoes = contarPorcentagens(random_nums, objects.length);

        let chaves = Object.keys(aparicoes);

        let combinacao = []
    

        for(let chave of chaves){

            let percentual = aparicoes[chave];
            let produto = objects[chave];

            if(produto.volume === 0)
                console.log(produto.nome)

            let qtde = parseFloat((parseInt(VolumeCaminhao)/parseFloat(produto.volume)) * percentual);

            if(!isNaN(qtde) || isFinite(qtde)){
                let comb = {
                    nome: produto.nome,
                    aparicao: percentual,
                    qtde: parseInt(qtde),
                    peso: parseFloat((qtde * produto.peso).toFixed(2)),
                    peso_unitario: produto.peso,
                    volume_unitario: parseFloat(produto.volume.toFixed(2)),
                    volume: parseFloat((produto.volume * qtde).toFixed(4)),
                    custoUnitario: produto.custo,
                    vendaUnitario: parseFloat(((((produto.custo) / (lucroDesejado * percentual))* produto.custo) + produto.custo).toFixed(2)),
                    lucroUnitario: parseFloat((((produto.custo) / (lucroDesejado * percentual))* produto.custo).toFixed(2)),
                    lucroTotal: parseFloat(((((produto.custo) / (lucroDesejado * percentual))* produto.custo) * qtde).toFixed(2)),
                }
    
    
                if(isFinite(comb.qtde) && !isNaN(comb.qtde) && isFinite(comb.peso) && !isNaN(comb.peso))
                    if(parseFloat(comb.lucroUnitario) <= parseFloat(maximoLucroUni)){
                        combinacao.push(comb)
                    }
            }
           

            
        }
    
        let somaPesos = 0;
        combinacao.forEach(comb => {
            somaPesos += parseFloat(comb.peso)
        })
    
        let somaVolume = 0;
        combinacao.forEach(comb => {
            somaVolume += parseFloat(comb.volume)
        })
    
        let somaLucro = 0;
        combinacao.forEach(comb => {
            if (!isNaN(comb.lucroUnitario)) {
                somaLucro += (comb.lucroUnitario * comb.qtde);
            }
        });

    
        
        let pesoMax = parseFloat(VolumeCaminhao)*parseFloat(capacidade)
    
        if(somaPesos < (pesoMax - (pesoMax * 0.05))){
            if(somaLucro >= (lucroDesejado - (lucroDesejado * 0.25))){
                combinacao.push(somaLucro)
                combinacao.push(somaVolume)
                return combinacao;
            }
        }else{
            combinacao.push(somaLucro)
        }
    }

    // function encherRestante(array, restante) {
    //     const randomIndex = Math.floor(Math.random() * array.length);
    //     const escolhido = array[randomIndex];
    //     restante = restante * 0.7;
    //     if(escolhido){
    //         escolhido.aparicao = ((escolhido.aparicao * restante) + escolhido.aparicao);
    //         escolhido.qtde = parseInt(escolhido.qtde + (escolhido.qtde * (restante - (restante*0.5))));
    //         escolhido.peso_unitario = (escolhido.peso);
    //         escolhido.peso = parseFloat((escolhido.qtde * escolhido.peso_unitario).toFixed(2));
    //         escolhido.volume = parseFloat((escolhido.volume_unitario * escolhido.qtde).toFixed(4));
    //         escolhido.vendaUnitario = parseFloat(((((escolhido.custoUnitario) / (lucroDesejado * escolhido.aparicao))* escolhido.custoUnitario) + escolhido.custoUnitario).toFixed(2));
    //         escolhido.lucroUnitario = parseFloat((((escolhido.custoUnitario) / (lucroDesejado * escolhido.aparicao))* escolhido.custoUnitario).toFixed(2));
    //         escolhido.lucroTotal = parseFloat(((((escolhido.custoUnitario) / (lucroDesejado * escolhido.aparicao))* escolhido.custoUnitario) * escolhido.qtde).toFixed(2)); 
    //     }
    // }
    
    function contarPorcentagens(vetor, tamanhoTotal) {
        var contagem = {};

        for (var i = 0; i < vetor.length; i++) {
            var numero = vetor[i];

            if (contagem[numero]) 
                contagem[numero]++;
            else 
                contagem[numero] = 1;
        }
    
        for (var numero in contagem) 
            contagem[numero] = (contagem[numero] / tamanhoTotal);
        return contagem;
    }
    
    return (
        <div className='Simulacao'>
            <h1 className='custom-cursor Simulacao-title'>Simulação De Lucro</h1>
            <div className='PriceWannaWin'>
                <h2>Quanto de lucro deseja ganhar?</h2>
                <input 
                    type='number' 
                    placeholder='Informe o lucro desejado' 
                    value={lucroDesejado}
                    onChange={(e) => setLucroDesejado(e.target.value)}
                />
                <h3>Capacidade de peso por metros cubicos</h3>
                <input 
                    type='number' 
                    placeholder='Informe o peso máximo'
                    value={capacidade}
                    onChange={(e) => setCapacidade(e.target.value)} 
                />
                <h3>Capacidade em metros cubicos</h3>
                <input 
                    type='number' 
                    placeholder='Informe a capacidade do caminhão'
                    value={VolumeCaminhao}
                    onChange={(e) => setVolumeCaminhao(e.target.value)} 
                />
                <h3>Máximo de lucro sobre produto</h3>
                <input 
                    type='number' 
                    placeholder='Informe a capacidade do caminhão'
                    value={maximoLucroUni}
                    onChange={(e) => setMaximoLucroUni(e.target.value)} 
                />
                <button onClick={handleGerarPossibilidades}>Gerar Possibilidades</button>
            </div>
            <RespostaSimulacao combinations={combinations} />
        </div>
    );
}

export default Simulacao;
