// ConfirmarProduto.js
import React from "react";
import { adicionarProduto } from "./firebase";

export default function ConfirmarProduto(props) {
    const { nome, precoDeFabrica, IPI, ICMS, Largura, Comprimento, Altura, show, handleCloseModal, handleCancelar } = props;

    const handleConfirmar = () => {
        const novoProduto = {
            nome,
            precoDeFabrica,
            IPI,
            ICMS,
            Largura,
            Comprimento,
            Altura
        };
        adicionarProduto(novoProduto); // Adiciona o produto ao Firebase
        handleCloseModal(); // Fecha o modal
    };

    return (
        <div className={show}>
            <div className="modal-confirmacao">
                <h2 className="modal-title">Dados do Produto:</h2>
                <div className="dados-produto">
                    <h4>Nome: {nome}</h4>
                    <h4>Preço de Fábrica: {precoDeFabrica}</h4>
                    <h4>IPI: {IPI}</h4>
                    <h4>ICMS: {ICMS}</h4>
                    <h4>Largura: {Largura}</h4>
                    <h4>Comprimento: {Comprimento}</h4>
                    <h4>Altura: {Altura}</h4>
                </div>

                <div className="modal-confirm">
                    <h1>As informações estão corretas?</h1>
                    <div className="modal-buttons">
                        <button className="no-button" onClick={handleCancelar}>Não</button>
                        <button className="yes-button" onClick={handleConfirmar}>Sim</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
