import React, { useEffect, useState } from 'react';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import { initializeApp } from 'firebase/app'; 
import firebaseConfig from './Components/firebaseConfig'; 
import RespostaSimulacao from './Components/RespostaSimulacao';

function Simulacao() {
    const [produtos, setProdutos] = useState([]);
    const [combinações, setCombinações] = useState([]);
    const [lucroDesejado, setLucroDesejado] = useState(null);
    const [pesoMaximo, setPesoMaximo] = useState(null);
    const [capacidadeCaminhao, setCapacidadeCaminhao] = useState(null);

    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                const app = initializeApp(firebaseConfig);
                const db = getFirestore(app);
                const produtosRef = collection(db, 'produtos');
                const produtosSnapshot = await getDocs(produtosRef);
                const produtosData = produtosSnapshot.docs.map(doc => ({
                    id: doc.id,
                    ...doc.data()
                }));
                setProdutos(produtosData);
            } catch (error) {
                console.error('Erro ao buscar os produtos:', error);
            }
        };
        fetchProdutos();
    }, []); 

    const gerarPossibilidades = () => {
        console.log("Gerando possibilidades...");
        const combinaçõesPossíveis = [];
        
        // Verifica se o lucro desejado é um número válido
        if (typeof lucroDesejado !== 'number' || isNaN(lucroDesejado)) {
            console.error("O lucro desejado não é um número válido.");
            return;
        }
        
        // Função auxiliar para gerar combinações recursivamente
        const gerarCombinações = (índice, combinaçãoAtual, pesoAtual, volumeAtual) => {
            // Verifica se a combinação atual atende ao lucro desejado
            const lucroAtual = calcularLucro(combinaçãoAtual);
            console.log("Lucro atual:", lucroAtual);
            console.log("Lucro desejado:", lucroDesejado);
            if (lucroAtual >= lucroDesejado) {
                console.log("Lucro desejado alcançado. Adicionando combinação...");
                combinaçõesPossíveis.push([...combinaçãoAtual]);
            }
    
            // Gera novas combinações incluindo o produto atual e chama recursivamente para os próximos produtos
            for (let i = índice; i < produtos.length; i++) {
                const produto = produtos[i];
                const novoPeso = pesoAtual + produto.Peso;
                const novoVolume = volumeAtual + produto.m3;
                
                if (novoPeso <= pesoMaximo && novoVolume <= capacidadeCaminhao) {
                    gerarCombinações(i + 1, combinaçãoAtual.concat(produto), novoPeso, novoVolume);
                }
            }
        };
    
        // Inicia a geração de combinações
        gerarCombinações(0, [], 0, 0);
    
        // Atualiza o estado combinações com as combinações geradas
        console.log("Combinações possíveis:", combinaçõesPossíveis);
        setCombinações(combinaçõesPossíveis);
    };
    
    

    const calcularLucro = (combinação) => {
        return combinação.reduce((total, produto) => {
            // Calcula o lucro para o produto considerando o preço final e a quantidade
            return total + (produto.PRECO_FINAL * produto.quantidade - produto.Peso * produto.quantidade);
        }, 0);
    };

 

    const handleLucroDesejadoChange = (e) => {
        const valor = parseFloat(e.target.value);
        if (!isNaN(valor)) {
            setLucroDesejado(valor);
        } else {
            setLucroDesejado(null);
        }
    };
    
    const handlePesoMaximoChange = (e) => {
        const valor = parseFloat(e.target.value);
        if (!isNaN(valor)) {
            setPesoMaximo(valor);
        } else {
            setPesoMaximo(null);
        }
    };
    
    const handleCapacidadeCaminhaoChange = (e) => {
        const valor = parseFloat(e.target.value);
        if (!isNaN(valor)) {
            setCapacidadeCaminhao(valor);
        } else {
            setCapacidadeCaminhao(null);
        }
    };
    
    return (
        <div className='Simulacao'>
            <h1 className='custom-cursor Simulacao-title'>Simulação De Lucro</h1>
            <div className='PriceWannaWin'>
                <h2>Quanto de lucro deseja ganhar?</h2>
                <input 
                    type='number' 
                    placeholder='Informe o lucro desejado'
                    value={lucroDesejado} 
                    onChange={handleLucroDesejadoChange} 
                />
                <h3>Peso máximo caminhão</h3>
                <input 
                    type='number' 
                    placeholder='Informe o peso máximo'
                    value={pesoMaximo} 
                    onChange={handlePesoMaximoChange} 
                />
                <h3>Capacidade</h3>
                <input 
                    type='number' 
                    placeholder='Informe a capacidade do caminhão'
                    value={capacidadeCaminhao} 
                    onChange={handleCapacidadeCaminhaoChange} 
                />
                <button onClick={gerarPossibilidades}>Gerar Possibilidades</button>
            </div>
            <RespostaSimulacao combinações={combinações} />
        </div>
    );
}

export default Simulacao;
