import React, { useEffect, useState } from 'react';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import { initializeApp } from 'firebase/app'; 
import firebaseConfig from './Components/firebaseConfig'; 
import RespostaSimulacao from './Components/RespostaSimulacao';

function Simulacao() {
    const [produtos, setProdutos] = useState([]);
    const [lucroDesejado, setLucroDesejado] = useState('');
    const [pesoMaximo, setPesoMaximo] = useState('');
    const [capacidadeCaminhao, setCapacidadeCaminhao] = useState('');
    const [combinations, setCombinations] = useState([]);

    useEffect(() => {
        const fetchProdutos = async () => {
            try {
                const app = initializeApp(firebaseConfig);
                const db = getFirestore(app);
                const produtosRef = collection(db, 'produtos');
                const produtosSnapshot = await getDocs(produtosRef);
                const produtosData = produtosSnapshot.docs.map(doc => ({
                    id: doc.id,
                    ...doc.data()
                }));
                setProdutos(produtosData);
            } catch (error) {
                console.error('Erro ao buscar os produtos:', error);
            }
        };
        fetchProdutos();
    }, []); 

    const handleGerarPossibilidades = () => {
        const infoProducts = produtos.map(p => ({
            custo: parseFloat(p.PRECO_FINAL),
            nome: p.Nome,
            peso: parseFloat(p.Peso),
            volume: parseFloat(p.m3)
        }));

        generateCombinations(infoProducts);
    };

    const generateCombinations = (products) => {
        const newCombinations = [];
        const maxProducts = Math.min(products.length, 5);

        // Gera todas as combinações possíveis de 2 a 5 produtos
        for (let i = 2; i <= maxProducts; i++) {
            for (let j = 0; j <= products.length - i; j++) {
                const combination = products.slice(j, j + i);
                const combinedWeight = combination.reduce((acc, curr) => acc + curr.peso, 0);
                const combinedVolume = combination.reduce((acc, curr) => acc + curr.volume, 0);

                // Verifica se a combinação cabe no caminhão
                if (combinedWeight <= pesoMaximo && combinedVolume <= capacidadeCaminhao) {
                    const combinedCost = combination.reduce((acc, curr) => acc + curr.custo, 0);
                    const averageCost = combinedCost / i;
                    const salePrice = averageCost * (1 + parseFloat(lucroDesejado) / 100);

                    // Verifica se o preço de venda não ultrapassa 150% do custo médio
                    if (salePrice <= averageCost * 1.5) {
                        newCombinations.push({
                            produtos: combination.map(prod => prod.nome),
                            volume_t: combinedVolume.toFixed(2),
                            peso_t: combinedWeight.toFixed(2),
                            custo_uni: averageCost.toFixed(2),
                            venda_uni: salePrice.toFixed(2),
                            lucro_uni: (salePrice - averageCost).toFixed(2),
                            lucro_t: ((salePrice - averageCost) * i).toFixed(2),
                            qtde: i
                        });
                    }
                }
            }
        }

        setCombinations(newCombinations);
    };
    
    return (
        <div className='Simulacao'>
            <h1 className='custom-cursor Simulacao-title'>Simulação De Lucro</h1>
            <div className='PriceWannaWin'>
                <h2>Quanto de lucro deseja ganhar?</h2>
                <input 
                    type='number' 
                    placeholder='Informe o lucro desejado' 
                    value={lucroDesejado}
                    onChange={(e) => setLucroDesejado(e.target.value)}
                />
                <h3>Peso máximo caminhão</h3>
                <input 
                    type='number' 
                    placeholder='Informe o peso máximo'
                    value={pesoMaximo}
                    onChange={(e) => setPesoMaximo(e.target.value)} 
                />
                <h3>Capacidade</h3>
                <input 
                    type='number' 
                    placeholder='Informe a capacidade do caminhão'
                    value={capacidadeCaminhao}
                    onChange={(e) => setCapacidadeCaminhao(e.target.value)} 
                />
                <button onClick={handleGerarPossibilidades}>Gerar Possibilidades</button>
            </div>
            <RespostaSimulacao combinations={combinations} />
        </div>
    );
}

export default Simulacao;
