import React, { useEffect } from 'react';
import SearchBar from './Components/SearchBar';
import Produtos from './Components/Produtos';
import { getFirestore, collection, getDocs } from 'firebase/firestore';
import firebaseConfig from '././firebaseConfig'; // Importe suas configurações do Firebase

function Gerenciamento() {
    useEffect(() => {
        // Função assíncrona para buscar e exibir dados da coleção 'produtos'
        const fetchProdutos = async () => {
            try {
                // Inicialize o Firestore com suas configurações do Firebase
                const db = getFirestore();

                // Referência para a coleção 'produtos'
                const produtosRef = collection(db, 'produtos');

                // Obtenha todos os documentos da coleção 'produtos'
                const produtosSnapshot = await getDocs(produtosRef);

                // Mapeie os documentos e exiba no console
                produtosSnapshot.forEach((doc) => {
                    console.log(doc.id, ' => ', doc.data());
                });
            } catch (error) {
                console.error('Erro ao buscar os produtos:', error);
            }
        };

        // Chame a função para buscar os produtos ao montar o componente
        fetchProdutos();
    }, []); // Array de dependências vazio para garantir que a chamada seja feita apenas uma vez

    return (
        <div className='Gerenciamento'>
            <h1>Barra de Pesquisa</h1>
            <SearchBar placeholder='Digite o nome do item' />
            <Produtos />
        </div>
    );
}

export default Gerenciamento;
