import React, { useState } from "react";
import ConfirmarProduto from "./ConfirmarProduto";
import { adicionarProduto } from "./firebase";

export default function Formulario() {
    const [showModal, setShowModal] = useState(false);

    const handleCreateProduct = () => {
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };


    return (
        <div className="FormularioProduto">
            <div className="form-inputs">
                <label>Nome do Produto</label>
                <input type="text" className="input" placeholder="*" id="nome"/>
            </div>
            <div className="form-inputs">
                <label>Preço de Fábrica</label>
                <input type="number" className="input" placeholder="*" id="preco-de-fabrica"/>
            </div>

            <div className="form-inputs">
                <label>IPI%</label>
                <input type="number" className="input" placeholder="*" id="IPI"/>
            </div>

            <div className="form-inputs">
                <label>ICMS</label>
                <input type="number" className="input" placeholder="*" id="ICMS"/>
            </div>

            <div className="form-inputs">
                <label>Largura da Caixa</label>
                <input type="number" className="input" placeholder="cm" id="Largura"/>
            </div>

            <div className="form-inputs">
                <label>Comprimento da Caixa</label>
                <input type="number" className="input" placeholder="cm" id="Comprimento"/>
            </div>

            <div className="form-inputs">
                <label>Altura da Caixa</label>
                <input type="number" className="input" placeholder="cm" id="Altura"/>
            </div>

            <button onClick={handleCreateProduct} id="submit">Criar Produto</button>

            <ConfirmarProduto 
                show={showModal ? "modal" : "modal d-none"} 
                handleCloseModal={handleCloseModal} 
                nome="name" 
                precoDeFabrica="name" 
                IPI 
                ICMS="name" 
                Largura="name" 
                Comprimento="name" 
                Altura="name"
            />
        </div>


    )
}
