import firebase from 'firebase/app';
import 'firebase/firestore';

const firebaseConfig = {
  apiKey: "SuaApiKey",
  authDomain: "SeuAuthDomain",
  projectId: "SeuProjectId",
  storageBucket: "SeuStorageBucket",
  messagingSenderId: "SeuMessagingSenderId",
  appId: "SuaAppId"
};

// Inicialize o Firebase
firebase.initializeApp(firebaseConfig);

// Referência para a coleção 'produtos'
const produtosCollection = firebase.firestore().collection('produtos');

// Função para adicionar um produto
export const adicionarProduto = async (produto) => {
  try {
    await produtosCollection.add(produto);
    console.log('Produto adicionado com sucesso!');
  } catch (error) {
    console.error('Erro ao adicionar produto:', error);
  }
};

// Função para editar um produto pelo ID
export const editarProduto = async (id, novosDados) => {
  try {
    await produtosCollection.doc(id).update(novosDados);
    console.log('Produto editado com sucesso!');
  } catch (error) {
    console.error('Erro ao editar produto:', error);
  }
};

// Função para obter todos os produtos
export const obterTodosProdutos = async () => {
  try {
    const snapshot = await produtosCollection.get();
    const produtos = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    return produtos;
  } catch (error) {
    console.error('Erro ao obter produtos:', error);
    return [];
  }
};
