import React from "react";

export default function RespostaSimulacao(props) {
  const {combinations} = {}
  console.log(combinations)
  return (
    <div className="RespostaSimulacao">
      <div className="RespostaSimulacaoHeader">
        <img
          className="moneyIcon"
          src="https://static.vecteezy.com/system/resources/previews/011/660/689/original/3d-money-icon-illustration-png.png"
          alt="Ícone de dinheiro"
        />
        <h1 className="SimulacoesTitle">SIMULAÇÕES</h1>
        <img
          className="moneyIcon"
          src="https://static.vecteezy.com/system/resources/previews/011/660/689/original/3d-money-icon-illustration-png.png"
          alt="Ícone de dinheiro"
        />
      </div>

      {combinations.map((combinacao, index) => (
        <div className="combinacao" key={index}>
          {/* Renderizar lista de produtos */}
          {combinacao.map((produto, produtoIndex) => {
            if (produtoIndex < combinacao.length - 4) {
              return (
                <div className="produto-da-combinacao" key={produtoIndex}>
                  <h3>{produto.nome}  -  {produto.qtde} unidades</h3>
                  <h4>Valor Custo: R${produto.custoUnitario}</h4>
                  <h4>Valor Venda: R${produto.vendaUnitario}</h4>
                  <h4>Volume: {produto.volume.toFixed(2)}m³</h4>
                </div>
              );
            }
            return null;
          })}
          {/* Renderizar soma dos custos */}
          <div key={`${index}-valores`} className="valores">
            <div className="div-flex">
              <h3 className="custo">CUSTO TOTAL: R${(combinacao[combinacao.length - 3].SOMA_CUSTO).toFixed(2)}</h3>
              <h3>LUCRO TOTAL: R${(combinacao[combinacao.length - 4].SOMA_LUCRO).toFixed(2)}</h3>
            </div>
            <div className="div-flex">
              <h4>VOLUME: {(combinacao[combinacao.length - 2].SOMA_VOLUME).toFixed(2)}m³</h4>
              <h4>PESO: {(combinacao[combinacao.length - 1].SOMA_PESO).toFixed(2)}Kg</h4>
            </div>

          </div>
        </div>
      ))}

    </div>
  );
}
